sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    var coursesGr = new GlideRecord('x_1588110_mock_s_0_course');

    // Adiciona a query para buscar cursos ativos com o nome especificado
    coursesGr.addActiveQuery();
    coursesGr.addQuery('name', current.x_1588110_mock_s_0_path);
    coursesGr.query();

    // Itera sobre os cursos encontrados
    while (coursesGr.next()) {
        // Verifica se já existe um registro de progresso para o funcionário e curso
        var progressGr = new GlideRecord('x_1588110_mock_s_0_m2m_progress');
        progressGr.addQuery('x_1588110_mock_s_0_intern', current.x_1588110_mock_s_0_employee);
        progressGr.addQuery('x_1588110_mock_s_0_course', coursesGr.sys_id);
        progressGr.query();

        // Se não existir, cria um novo registro de progresso
        if (!progressGr.next()) {
            progressGr.initialize();
            progressGr.x_1588110_mock_s_0_intern = current.x_1588110_mock_s_0_employee;
            progressGr.x_1588110_mock_s_0_course = coursesGr.sys_id;
            progressGr.insert();
        }
    }
})(current, previous);
