sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading) {
		return;
	}
	// if new email is empty, clear the user_name value
	if(newValue === ''){
		g_form.clearValue('user_name');
		return;
	}

	var email = g_form.getValue('email'); // access the current form and get the value of 'email'
	if (email.indexOf('@extreme.digital') == -1) { // indexOf verify the email, if not contains return '-1' 
		// clear invalid email
		g_form.clearValue('email');
		// alert to enter right format
		g_form.addErrorMessage('Add email containing @extreme.digital');
		g_form.clearValue('user_name'); // acess the current form and clean the user_name value
	} else {
		var id = email.split('@', 1); // split the email getting the part before the '@' \ save in var id
		g_form.setValue('user_name', id); // acess the current form and set the value of 'user_name' to id
	}

}
