sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/) {

	// Add your code here
	log.info('rodou o scheduled import');
	gs.addInfoMessage('rodou');
	var project = source.u_projeto;
	var assign = source.u_task;

	var projects = new GlideRecord('x_1588110_mock_s_0_project');
	projects.addQuery('number', project); // acha o projeto pelo number
	projects.query();

	if (projects.next()) {
		// se o projeto existir, associa com o sys_id
		target.parent = projects.sys_id;

		var assigns = new GlideRecord('x_1588110_mock_s_0_assignment');
		assigns.addQuery('number', assign);
		assigns.query();

		if (assigns.next()) {
			target.assignment = assigns.sys_id;
			ignore = false;
			return;
		} else {
			log.info('Atribuição não encontrada: ' + assign + ' para o Projeto = ' + project);
			gs.addInfoMessage('Atribuição não encontrada: ' + assign + ' para o Projeto = ' + project);
			ignore = true;
			return;
		}
	} else {
		log.info('Projeto não encontrado: ' + project);
		ignore = true;
		return;
	}
})(source, map, log, target);
